import React from 'react';
import './App.css';


export default class App extends React.Component{
  // array of items
  // property that holds an empty string or value 
  // state -- setState??
  // list out the items
  // onsubmit 
  // onChange 
  // bind?

  constructor(props) {
    super(props)

    this.state = {
      term: '',
      items: []
    }
  }

  onChange = (event) => {
    this.setState({term: event.target.value})
  }

  onSubmit = (event) => {
    event.preventDefault()

    items: [...this.state.items, this.state]
  }

  render() {
  return (
    <div>
    <div className="header">
      <div clssName="wrapper">
        <img src="" />
        <p className="title">Road to Hire's Note taking app</p>
      </div>
    </div>

    <div className="app">
      <div className="app-wrapper"></div>
      <p className="app-title">To-Do List</p>
      <form className="App">
        <input value = {this.state.term} onChange = {this.onChange} placeholder="Type here to add a task" type="text" id="box" />
        < br />
      </form>
      <br />
    </div>
    </div>
  );
}
}
